sys_variable_value.value:
(function execute(inputs, outputs) {

    var grLang = new GlideRecord('sys_language');  
    grLang.addQuery('name', inputs.language);
    grLang.query();
    if (grLang.next()) {
        outputs.sys_language = grLang.sys_id;
    } else {
        grLang.initialize();
        grLang.name = inputs.language;
        lang_id = grLang.insert();

//      	var grLang2 = new GlideRecord('sys_language');
//      grLang2.get(lang_id);
      	outputs.sys_language = lang_id;
    }


})(inputs, outputs);

